#macro (__download__displayOption $info)
(% class="download-option" %)(((
== ${info.title} ==
[[{{icon name="download"/}}Download $!{services.distribution.distributionExtension.id.version}.$!{info.type}>>http://nexus.cs.toronto.edu/nexus/content/repositories/releases/org/phenotips/$!{info.id}/$!{services.distribution.distributionExtension.id.version}/$!{info.id}-$!{services.distribution.distributionExtension.id.version}.$!{info.type}||class="downloadButton"]]

$!{info.desc}

=== Installation and usage ===
$!{info.info}
)))
#end



#macro (__download__displayOptions)
#foreach ($o in $downloadOptions)
  #__download__displayOption($o)
#end
(% class="clearfloats" %)((()))
#end
